#Menu
def user_menu(journalists)
  run=true
  while run
  puts "\n"
  puts "\n"
  puts '-' * 50
  puts "\n"
  puts "\n"
  puts "Choisissez une question parmi la liste suivante:"
  puts "\n"
  puts "1. Combien y a-t-il de handle dans cette array ?"
  puts "2. Quel est le handle le plus court de cette liste ?"
  puts "3. Combien y-a-t'il de handle contenant 5 caractères?"
  puts "4. Combien commencent par une majuscule?"
  puts "5. Trie la liste de handle par ordre alphabétique"
  puts "6. Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)"
  puts "7. Quelle est la position dans l'array de la personne @epenser ?"
  puts "8. Quelle est la répartition des handles par taille?"
  puts "9. Sortir du programme"
  puts "\n"
  puts "\n"
  puts '-' * 50
  puts "\n"
  puts "\n"
  puts "\n"
  puts "> "
  question=gets.chomp.to_i
    case question
    when 1
      comptage(journalists)
    when 2
      short_handle(journalists)
    when 3
      five(journalists)
    when 4
      majuscules(journalists)
    when 5
      alpha_order(journalists)
    when 6
      size_order(journalists)
    when 7
      position_handle(journalists)
    when 8
      repartition_size(journalists)
    else
      hasta_la_vista
      run=false
    end
  end
end


# Combien de handle dans array
def comptage(number)
  puts '-' * 50
  puts "Question 1:"
  puts "Combien y a-t-il de handle dans cette array ?"
  puts '-' * 50
  puts "\n"
  puts "\n"
    puts "Réponse: #{number.count}"
end

# Le handle le plus court de la liste
def short_handle(number)
  puts '-' * 50
  puts "Question 2:"
  puts "Quel est le handle le plus court de cette liste ?"
  puts '-' * 50
  puts "\n"
  puts "\n"
  puts "Réponse: #{number.sort_by(&:length)[0]}"
end

# Nombre de handle à 5 caractères
def five(table)
  puts '-' * 50
  puts "Question 3:"
  puts "Combien y-a-t'il de handle contenant 5 caractères?"
  puts '-' * 50
  puts "\n"
  puts "\n"
  table5=[]
  table.each do |i|
    i.size
    if i.size==6
    table5 << i
  end
  end
  puts "Réponse: #{table5.count}"
end

# Majuscules
def majuscules(table)
  puts '-' * 50
  puts "Question 4:"
  puts "Combien commencent par une majuscule?"
  puts '-' * 50
  puts "\n"
  puts "\n"
  puts "Réponse: #{table.count {|e| e =~ /^@[A-Z]/}}"
end

# Ordre alphabétique
def alpha_order(table)
  puts '-' * 50
  puts "Question 5:"
  puts "Trie la liste de handle par ordre alphabétique"
  puts '-' * 50
  puts "\n"
  puts "\n"
  puts "Réponse: #{table.sort_by(&:downcase)}"
end

# Ordre de grandeur
def size_order(table)
  puts '-' * 50
  puts "Question 6:"
  puts "Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)"
  puts '-' * 50
  puts "\n"
  puts "\n"
  puts "Réponse; #{table.sort_by(&:length)}"
end

# La position d'un handle
def position_handle(table)
  puts '-' * 50
  puts "Question 7:"
  puts "Quelle est la position dans l'array de la personne @epenser ?"
  puts '-' * 50
  puts "\n"
  puts "\n"
  puts "Réponse: #{table.find_index("@epenser")}"
end

# Répartition
def repartition_size(table)
  puts '-' * 50
  puts "Question 8:"
  puts "Quelle est la répartition des handles par taille?"
  puts '-' * 50
  puts "\n"
  puts "\n"
    for i in (4..33)
      new_list = []
      table.each do |username|
        if username.size == i
          new_list << username
        end
      end
      if new_list.count > 0
        puts "Le nombre de handle avec #{i-1} caratères : #{new_list.count}"
      end
    end
end

def hasta_la_vista
  puts "HASTA LA VISTA BYE-BYE !"
end

user_menu(journalists)

